// We will be using Solidity version 0.5.3
pragma solidity 0.5.16;

// Importing OpenZeppelin's SafeMath Implementation
import "./SafeMath.sol";
import "./Diamond.sol";  // Token Contract
import "./ArtERC20.sol"; // ERC20 contract
import "./InsertionSorter.sol";

contract ClosedAuctionBox{
    Diamond private token;
    address internal admin;
    InsertionSorter public array_sorter;
    
    HashedClosedAuction[] public auctions; 
    constructor(InsertionSorter _array_sorter) public {
        array_sorter = _array_sorter;
        admin = msg.sender;
    }   
    
    modifier onlyOwner {
        require(msg.sender == admin);
        _;
    }
    function createAuction ( ArtERC20Token _erc20Token,
        Diamond _token,
        uint _biddingTime,
        address payable _beneficiary,
        uint _startPrice,
        uint256 _token_id,
        uint256 _auctionMaximumBidOverwriteCount)  onlyOwner public{
        // set the new instance
        HashedClosedAuction closedAuction = new HashedClosedAuction(array_sorter, _erc20Token,
        _token, msg.sender, _biddingTime, _beneficiary, _startPrice, _token_id, _auctionMaximumBidOverwriteCount);
        // push the auction address to auctions array
        auctions.push(closedAuction);
    }
    
    function returnAllAuctions() public view returns(HashedClosedAuction[] memory){
        return auctions;
    }
}


contract HashedClosedAuction {
    InsertionSorter public array_sorter;
    // * Data Structures *
    // -----------------------
    // Auction possible states
    enum AuctionStates {Init, AcceptBids, ProveBids, RevealWinner}

    // Bid Data structure
    struct BidData {
        // We store bid value only at `ProveBids` stage to determine user with highest bid.
        uint256 provedBidValue; 
        // We store timestamp to determine first highest bidder at `ProveBids` stage if bids values is equal.
        uint bidTimestamp;
        // Hash of bid value and salt. Bidder generates it by itself.
        bytes32 bidHash;
        // Count of bids made by this bidder
        uint bidsCounter;
    }

    // * Auction parameters *
    // -----------------------
    address private owner;  // ART Token owner
    address payable public beneficiary;  // ""
    // Parameters of the auction. Times are either
    // absolute unix timestamps (seconds since 1970-01-01)
    // or time periods in seconds.
    Diamond private token;
    ArtERC20Token private erc20Token;  // ERC20 Token used for make bids.
    uint public auctionEndTime;
    AuctionStates public auctionState; // Current auction state
    uint public auctionMinimalBidPrice;  // Minimal bid price.
    uint256 public auctionMaximumBidOverwriteCount;  // Maximal bid overwrite count for single bidder

    // ART Token parameters for check existing of it and next transfer ownership to winner, when auction finished
    uint256 public token_id;

    // * Different variables *
    // -----------------------

    // Address of auction's winner.
    // It will update when auction becomes to `RevealWinner` state .
    // Defaults to zero address in the blockchain (0x000000...000).
    address public winner;
    
    // When auction comes to `ProveBids` state, we will save highest proved bid to this variable
    address private provedUserWithHighestBid;
    uint private provedHighestBid;

    // Mapping Bidder address to his bid.
    // At `AcceptBids` state we store info about bid to this mapping.
    // At `ProveBids` state we are using this data to validate bids.
    mapping(address => BidData) private biddersBidData;
    
    // Array of users that successfully proved their bids at `ProveBids` auction stage.
    address[] private usersBidsProved;
    uint256[] private usersBidsProvedValues;
    
    
    // Set to true at the end, disallows any change.
    // By default initialized to `false`.
    bool public is_ended;

    // Events that will be emitted on changes.
    event AuctionEnded(address winner, uint amount);

    // The following is a so-called natspec comment,
    // recognizable by the three slashes.
    // It will be shown when the user is asked to
    // confirm a transaction.

    /// Create a simple auction with `_biddingTime`
    /// seconds bidding time on behalf of the
    /// beneficiary address `_beneficiary`.
    constructor(
        InsertionSorter _array_sorter,
        ArtERC20Token _erc20Token,
        Diamond _token,
        address _owner,
        uint _biddingTime,
        address payable _beneficiary,
        uint _startPrice,
        uint256 _token_id,
        uint256 _auctionMaximumBidOverwriteCount
            ) public {
        array_sorter = _array_sorter;
        token = _token;
        erc20Token = _erc20Token;
        beneficiary = _beneficiary;
        auctionEndTime = now + _biddingTime;
        auctionMinimalBidPrice = _startPrice;
        owner = _owner;
        token_id = _token_id;
        auctionMaximumBidOverwriteCount = _auctionMaximumBidOverwriteCount;
        auctionState = AuctionStates.Init;
    }

    modifier tokenExist {
            require(token.isExists(token_id), "Token doesn't exist");
        _;
    }

    function bid(bytes32 hash) tokenExist public {
        // Revert the call if auction bids accept period is over.
        require(auctionState == AuctionStates.AcceptBids, "Auction doesn't accept bids at this moment.");
        // Revert the call if the auction is expired
        require(now <= auctionEndTime,"Auction already ended.");
        require(!is_ended, "auctionEnd has already been called.");
        
        // Revert the call if winner already determined
        require(winner == address(0), "Winner already determined");
        
        // Revert the call if passed hash is not needed size
        require(hash.length == 32, "Hash should be fixed size!");
        
        BidData memory senderBidData = biddersBidData[msg.sender];
        // Revert the call if bids overwrite counter is over for current bidder
        require(senderBidData.bidsCounter <= auctionMaximumBidOverwriteCount, "Your total bids counter is over. Saved only latest bid");

        senderBidData.bidTimestamp = now;
        senderBidData.bidHash = hash;
        senderBidData.bidsCounter = senderBidData.bidsCounter + 1;
         // Write current bid to biddersBidData
        biddersBidData[msg.sender] = senderBidData;

    }
    
    // Prove bid at `ProveBids` stage of auction.
    function proveBid(uint256 bidValue, string memory salt) tokenExist public {
        // Revert the call if auction bids prove period is over.
        require(auctionState == AuctionStates.ProveBids, "Auction doesn't allow prove bids at this moment.");
        
        // Revert the call if the auction is expired
        require(now <= auctionEndTime,"Auction already ended.");
        require(!is_ended, "auctionEnd has already been called.");
         
        // Revert the call if winner already determined
        require(winner == address(0), "Winner already determined");

        // Revert the call if bid less then minimal bid for this auction
        require(bidValue>=auctionMinimalBidPrice, "Bid value needs to be bigger then minimalBidPrice");

        // Revert the call if user's balance of specified ERC20 Token less then bid value
        require(erc20Token.balanceOf(msg.sender)>=bidValue, 
            "The balance of msg.sender is less then bid value!");
            
        // Revert the call if user did not allow the contract to transfer the value of the bet to the beneficiary if he becomes the winner
        require(erc20Token.allowance(msg.sender, address(this)) >= bidValue,
            "The user did not allow the contract to transfer the value of the bet to the beneficiary if he becomes the winner");

        BidData memory senderBidData = biddersBidData[msg.sender];
        // Revert the call if user already proved his bid.
        require(senderBidData.provedBidValue == 0, "You already prove your bid.");
        // Revert the call if user doesn't make any bids in this auction, but tries prove non-existent bid.
        require(senderBidData.bidsCounter > 0, "You must make bid at least 1 time, before prove it!");
        // Revert the call if user bidValue and salt doesn't provide same hash for hash of bid made at `AcceptBids` auction stage
        require(senderBidData.bidHash == keccak256(abi.encodePacked(bidValue, salt)), "Prove bid failed. Invalid bidValue and Salt.");

        // Save bid value to db to determine next winner if contract can't transfer tokens to beneficiary from winner balance at `RevealWinner` stage.
        senderBidData.provedBidValue = bidValue;
        biddersBidData[msg.sender] = senderBidData;
        usersBidsProved.push(msg.sender);
        usersBidsProvedValues.push(bidValue);
        // Check current proved highest bidder and change it if bid timestamp < bid timestamp of current highest bidder and bidValue >= provedHighestBid
        if (bidValue>=provedHighestBid){
            if (bidValue == provedHighestBid){
                if (senderBidData.bidTimestamp < biddersBidData[provedUserWithHighestBid].bidTimestamp) {
                    provedUserWithHighestBid = msg.sender;
                    provedHighestBid = bidValue;
                }
            } else {
                provedUserWithHighestBid = msg.sender;
                provedHighestBid = bidValue;
            }
        }

        
    }

    // Changes auction state. Only owner can call this function
    function changeAuctionState(AuctionStates state) tokenExist public {
        // Revert call if sender is not owner of token.
        require(
            (auctionState == AuctionStates.Init && state == AuctionStates.AcceptBids) ||
            (auctionState == AuctionStates.AcceptBids && state == AuctionStates.ProveBids) ||
            (auctionState == AuctionStates.ProveBids && state == AuctionStates.RevealWinner), "Wrong state order");  // guarantees auction order
        require(msg.sender == owner);
        if (auctionState == AuctionStates.Init){
            require(token.getApproved(token_id) == address(this), "You must approve auction to operate with your token");
            token.transferFrom(token.ownerOf(token_id), address(this), token_id);  // guarantee that owner can't remove approval after init stage
            require(token.ownerOf(token_id) == address(this), "contract is not owner of token");
        }

        auctionState = state;
    }
    

    // Reveal winner at `RevealWinner` auction stage. Only owner can call this function
    function revealWinner() tokenExist public {
        // Revert call if sender is not owner of token.
        require(msg.sender == owner);
        // Revert the call if auction bids prove period is over.
        require(auctionState == AuctionStates.RevealWinner, "Auction doesn't allow reveal winner at this moment.");
        
        // Revert the call if the auction is expired
        require(now <= auctionEndTime,"Auction already ended.");
        require(!is_ended, "auctionEnd has already been called.");
         
        // Revert the call if winner already determined
        require(winner == address(0), "Winner already determined");
        
        if(erc20Token.balanceOf(provedUserWithHighestBid)>=provedHighestBid 
            && erc20Token.allowance(provedUserWithHighestBid, address(this))>=provedHighestBid)
        {
            // "Shut up and take my money!"
            erc20Token.transferFrom(provedUserWithHighestBid, beneficiary, provedHighestBid);
            // Make Auction Ended
            is_ended = true;
            emit AuctionEnded(provedUserWithHighestBid, provedHighestBid);
            // Transfer ownership
            token.transferFrom(address(this), provedUserWithHighestBid, token_id);
            winner = provedUserWithHighestBid;
            return();
            
        } 
         else {
            (usersBidsProvedValues, usersBidsProved) = array_sorter.sort(usersBidsProvedValues, usersBidsProved);
            
            
            for(uint i=usersBidsProved.length-1; i>=0; i--){
                address next_proved_winner = usersBidsProved[i];
                uint next_proved_winner_bid = usersBidsProvedValues[i];
                if(erc20Token.balanceOf(next_proved_winner)>=next_proved_winner_bid 
                    && erc20Token.allowance(next_proved_winner, address(this))>=next_proved_winner_bid)
                {
                    // "Shut up and take my money!"
                    erc20Token.transferFrom(next_proved_winner, beneficiary, next_proved_winner_bid);
                    // Make Auction Ended
                    is_ended = true;
                    emit AuctionEnded(next_proved_winner, next_proved_winner_bid);
                    // Transfer ownership
                    token.transferFrom(address(this), next_proved_winner, token_id);
                    winner = next_proved_winner;
                    return();
                    
                } 
            }
        }

        
    }



}
